package estudos3;

public class exercise03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=1;
		while (x<=100) {
			System.out.println("x= "+x);
			if (x%10==0) {
				System.out.println("!! Multiplo de 10 !!");
			} // end if
			x++;
			
		} // end while
		
		
	} // end main

} // end class